msg = {};
msg.payload = {};

msg.payload.temperatures = flow.get('temperatures');
msg.payload.is_loading = flow.get('is_loading') || false; // default false
msg.payload.is_enabled = flow.get('is_enabled'); // enabled by default
msg.topic = env.get("load_topic");

return msg;

temp_map = flow.get('temperatures');

if (typeof msg.topic !== 'undefined'){
    temp_map[msg.topic] = msg.payload;

}

return msg;

initialize:
// Code added here will be run once
// whenever the node is deployed.

flow.set('temperatures', {});

func:
msg1 = {"payload": false};
msg2 = {"payload": false};
msg3 = {"payload": false};


if (msg.topic == "Mischer/Automatik")
    msg1.payload = msg.payload;
if (msg.topic == "heizkreis/mischer/heiss")
    msg2.payload = msg.payload;
if (msg.topic == "heizkreis/mischer/kalt")
    msg3.payload = msg.payload;

/*
// This forces one state to be true
if (msg.topic == "Mischer/Automatik")
    msg1.payload = true;
if (msg.topic == "Mischer/Heiss")
    msg2.payload = true;
if (msg.topic == "Mischer/Kalt")
    msg3.payload = true;
*/

return [msg1, msg2, msg3];

global_temperatures = global.get('temperatures');
temperatures = msg.payload.temperatures;


// Load parameters from message
// TODO

// Rule 0: automatic or manual mode
is_enabled = msg.payload.is_enabled;

// Rule 1: temperature too high -> stop loading
is_hot = temperatures['sensors/brauchwasser/3'] > 45;

// Rule 2: temperature too low -> start loading
is_cold = temperatures['sensors/brauchwasser/1'] < 60;

// Rule 3: check if we have some sufficient supplier
loss = 2;
can_load = Object.values(temperatures).every(t => t <global_temperatures['sensors/heizkreis/1'] - loss );
load_end = temperatures['sensors/heizkreis/1'] < 60;

// Rule 4: Force loading if supplier is overheading
overheat_protection = global_temperatures['sensors/heizkreis/4'] > 71;

if (!is_enabled) {
    // manual mode -> do nothing automatically
    new_loading_state = msg.payload.is_loading;
}

else if (overheat_protection) {
    // protect the supplier by taking the hot water
    new_loading_state = true;
}

else if (is_hot) {
    // stop because buffer is full
    new_loading_state = false;
}

else if (load_end) {
    // disable as soon as we cannot load anymore
    new_loading_state = false;
}

else if (is_cold && can_load) {
    // start loading as soon as we are getting too cold
    new_loading_state = true;
}


else {
    new_loading_state = msg.payload.is_loading;
}


result = {};
result.payload = new_loading_state;
result.topic = msg.topic;


if (result.payload != msg.payload.is_loading) {
    //loading state changed
    reason = {}
    reason.payload = "";
    reason.payload += "is_hot: " + is_hot;
    reason.payload += " | is_cold: " + is_cold;
    reason.payload += " | can_load: " + can_load;
    reason.payload += " | overheat_protection: " + overheat_protection;
} else {
    reason = undefined;
}


return [result, reason];

temp_map = global.get('temperatures');

if (typeof msg.topic !== 'undefined'){
    temp_map[msg.topic] = msg.payload;

}

return msg;

