// parameters

// initialize 
locals = {}
locals.logs = []

locals.is_hot = 0
locals.is_cold = 0
locals.can_load = 0
locals.supplier_overheating = 0
locals.new_loading_state = 0

// read msg
is_loading = msg.args.is_loading
is_enabled = msg.args.is_enabled
old_state = msg.args.old_state
temperatures = msg.args.temperatures
global_temperatures = msg.args.global_temperatures
max_temperatures = msg.parameters.max_temperatures
min_temperatures = msg.parameters.min_temperatures
suppliers = msg.parameters.suppliers


// precalculations
locals.lowest_temp = Math.min(...Object.values(temperatures));


// Rule 1: temperature too high -> stop loading
for (var sensor_topic in max_temperatures){
    locals.is_hot |= temperatures[sensor_topic] > max_temperatures[sensor_topic]
}

// Rule 2: temperature too low -> start loading
for (var sensor_topic in min_temperatures){
    locals.is_cold |= temperatures[sensor_topic] < min_temperatures[sensor_topic]
}

// Rule 3: check if we have some sufficient supplier
for (var idx in suppliers){
    supplier = suppliers[idx]
    locals.supplier_temp = global_temperatures[supplier.sensor]
    locals.can_load |= (locals.supplier_temp > locals.lowest_temp + supplier.loss)
}


// Rule 4: Force loading if supplier is overheading
for (var idx in suppliers){
    supplier = suppliers[idx]
    locals.overheat_temp = global_temperatures[supplier.overheating_sensor]
    locals.supplier_overheating |= (locals.overheat_temp > supplier.overheating_temperature)
}

/*
// Apply rules
if (!is_enabled) {
    // manual mode -> do nothing automatically
    locals.new_loading_state = is_loading;
}
*/
if (locals.supplier_overheating) {
    // protect the supplier by taking the hot water
    locals.new_loading_state = true;

    } else if (locals.is_hot) {
    // stop because buffer is full
    locals.new_loading_state = false;

    } else if (!locals.can_load) {
    // disable as soon as we cannot load anymore
    locals.new_loading_state = false;

    } else if (locals.is_cold && locals.can_load) {
    // start loading as soon as we are getting too cold
    locals.new_loading_state = true;

    } else {
    locals.new_loading_state = is_loading
}

if (is_loading != locals.new_loading_state) {
    locals.logs.push(locals.new_loading_state ? "Start loading" : "Stop loading");
    }
    else {
        locals.logs.push(locals.new_loading_state ? "Remain loading" : "Remain idle");
    }

// Logging
locals.new_state = {
    "is_loading": locals.new_loading_state,
    "is_hot": locals.is_hot, 
    "is_cold": locals.is_cold, 
    "can_load": locals.can_load, 
    "supplier_overheating": locals.supplier_overheating
}


if (old_state != locals.new_state) {
    flow.set('state', locals.new_state);
}


// initialize msg
msg = {
    "topic": msg.topic,
    "payload": locals.new_loading_state
}

return [msg, locals]

