// calculation
in_out_delta = target_room_temperature - outdoor_temperature

if (in_out_delta > 0) {
    // Some ease-off function from delta_t to requested temperature
    msg.payload = slope * in_out_delta ** delta_exponent + target_room_temperature + offset
} else {
    msg.logs.push("Calculation rule for negative delta is not defined");
    msg.payload = undefined
}

if (msg.payload == undefined) {
    msg.logs.push("Fallback value used");
    msg.payload = fallback;
}

// max temperature limit
msg.payload = Math.min(msg.payload, max_temperature);
msg.payload = Math.round(msg.payload * 100) / 100; // round for the visuals

return msg;

