func:
// parameters
min_delta_supply_over_room_temperature = 4 // Â°C die das Heizungswasser Ã¼ber der geforderten Raumtemperatur sein muss, damit Heizbetrieb aktiviert wird
min_delta_indoor_outdoor = 3 // Mindestunterschied zwischen Raumsoll und Aussen bis Heizung einschaltet
supply_temperature_while_charging = 50 // Min Versorgungstemperatur in [Â°C], wÃ¤hrend ladevorgang


// initialize variables
debug = {}
debug.logs = []
target_room_temperature = msg.args.target_room_temperature
outdoor_temperature = msg.args.outdoor_temperature
supply_temperature = msg.args.supply_temperature
supply_is_charing = msg.args.supply_is_charing

on_off = true;

// sanity checks
target_room_temperature == undefined ? debug.logs.push("target_room_temperature is undefined") : {};
outdoor_temperature == undefined ? debug.logs.push("outdoor_temperature is undefined") : {};
supply_temperature == undefined ? debug.logs.push("supply_temperature is undefined") : {};
supply_is_charing == undefined ? debug.logs.push("supply_is_charing is undefined") : {};

// Turn off if the supply temperature is too low
if (supply_temperature < target_room_temperature + min_delta_supply_over_room_temperature) {
    debug.logs.push("Turn off because temperature is too low");          
    on_off = false;
}


// Turn off if supplier is almost empty and charging
else if (supply_is_charing && supply_temperature < supply_temperature_while_charging) {
    debug.logs.push("Turn off because supplier is charging and low");      
    on_off = false;

}

// Turn off heating, if it get too warm outside
else if (target_room_temperature - outdoor_temperature < min_delta_indoor_outdoor) {
    debug.logs.push("Turn off because outside is too warm");                
    on_off = false;
}



// build message
msg = {
    "topic": "pumpe/heizkreis/ein",
    "payload": on_off
}

return [msg, debug];

// parameters
Kp = msg.parameters.Kp;
Ki = msg.parameters.Ki;
Kd = msg.parameters.Kd;
hysteresis = msg.parameters.hysteresis;

// arguments
setpoint = msg.args.setpoint;
process_variable = msg.args.actual_output_temperature;

// state variables
previous_error = context.get("previous_error");
previous_process_variable = context.get("previous_process_variable");
integral = context.get("integral");



/*
Calling this method will start a periodic loop

The feedback controller is based on a simple PD controller
*/

if (setpoint == undefined) {
    msg.warn = "No target temperature set"
    return msg
}

if (process_variable == undefined) {
    msg.warn ="Unknown output temperature"
    return msg
}

// Calculate the targeted temperature change for this period
dt = parseFloat(msg.period);
error = setpoint - process_variable;
derivative = (previous_process_variable - process_variable) / dt;
integral = integral + error * dt;
delta = (Kp * error + Kd * derivative + Ki * integral);


// store for the next iteration
context.set("previous_process_variable", process_variable);
context.set("previous_error", error);
context.set("integral", integral);


msg.topic = delta > 0 ? "mischer/heizkreis/heiss" : "mischer/heizkreis/kalt";
msg.payload = false;
msg.delay = 0;
msg.proportional_error = error;
msg.derivative_error = derivative;
msg.integral_error = integral;



msg.target_temperature_delta = delta;

if (Math.abs(delta) > hysteresis) {

    cycle_period = msg.period;
    temperature_to_time = 3.3 // seconds per degree

    // Topic depends on if we need to open or close the mixer
    msg.payload = true;

    // Calc the duty cycle
    desired_time = temperature_to_time * Math.abs(delta);
    duty_time = Math.min(desired_time, dt);
    msg.delay = duty_time;

}

return msg;

